import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");

  // Fonction pour récupérer les todos depuis le backend
  const fetchTodos = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/todos");
      setTodos(response.data);
    } catch (error) {
      console.error("Erreur lors de la récupération des todos :", error);
    }
  };

  // Charger les todos lors du montage du composant
  useEffect(() => {
    fetchTodos();
  }, []);

  // Fonction pour ajouter un nouveau todo
  const addTodo = async () => {
    if (newTodo.trim() === "") return;

    try {
      const response = await axios.post("http://127.0.0.1:8000/todos", {
        title: newTodo,
        completed: false,
      });

      setTodos([...todos, response.data]);
      setNewTodo("");
    } catch (error) {
      console.error("Erreur lors de l'ajout du todo :", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Todo List</h1>
      <input
        type="text"
        placeholder="Ajouter une tâche"
        value={newTodo}
        onChange={(e) => setNewTodo(e.target.value)}
      />
      <button onClick={addTodo}>Ajouter</button>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            {todo.title} {todo.completed ? "(Fait)" : "(À faire)"}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
